from dotenv import load_dotenvimport os# -*- coding: utf-8 -*-# Cargar variables de entornoload_dotenv(dotenv_path=os.path.join(os.path.dirname(os.path.dirname(__file__)), '.env'))# Configuración de MongoDBMONGODB_URI = os.getenv("MONGODB_URI", "mongodb://localhost:27017")DB_NAME = os.getenv("DB_NAME", "martin_llm")# Nombres de coleccionesCOLLECTION_CONVERSATIONS = "conversations"COLLECTION_MESSAGES = "messages"# SQLite backup path (por si falla MongoDB)DB_PATH = os.path.join(    os.path.dirname(os.path.dirname(os.path.dirname(os.path.dirname(__file__)))),    'data',    'chat_history.db')# Configuración de PostgreSQLDB_CONFIG = {    'host': os.getenv('DB_HOST', 'localhost'),    'port': int(os.getenv('DB_PORT', 5432)),    'database': os.getenv('DB_NAME', 'martin_llm'),    'user': os.getenv('DB_USER', 'postgres'),    'password': os.getenv('DB_PASSWORD', '')}# Construir URL de conexión SQLAlchemyDB_URL = f"postgresql://{DB_CONFIG['user']}:{DB_CONFIG['password']}@{DB_CONFIG['host']}:{DB_CONFIG['port']}/{DB_CONFIG['database']}"# Configuración de OllamaOLLAMA_HOST = os.getenv("OLLAMA_HOST", "http://localhost:11434")DEFAULT_MODEL = os.getenv("DEFAULT_MODEL", "llama2")# Configuración de la aplicaciónMODEL_NAME = os.getenv('MODEL_NAME', 'llama3')TEMPERATURE = float(os.getenv('TEMPERATURE', 0.7))MAX_CONTEXT_LENGTH = int(os.getenv('MAX_CONTEXT_LENGTH', 1000))# Otros ajustesDEBUG = os.getenv("DEBUG", "False").lower() == "true"LOG_LEVEL = os.getenv("LOG_LEVEL", "INFO")